// returns the extension we can switch on later

const binaryKey = 'data';          // <-- the key of the binary object from Telegram/Download node
const item      = items[0].binary[binaryKey];

// 1) prefer the explicit field if it exists
let ext = item.fileExtension || '';

// 2) fallback: take everything after the last dot of fileName / file_path
if (!ext) {
  const fileName = item.fileName || item.file_path || '';
  ext = fileName.split('.').pop().toLowerCase();
}

// 3) make sure we have something reasonable
const allowed = ['jpg', 'jpeg', 'png', 'pdf', 'csv'];
ext = allowed.includes(ext) ? ext : 'unknown';

return [{ json: { fileExt: ext } }];
